function verificarRespuestas(){const correctAnswers={hardware:["Mouse","Teclado","Monitor"],software:["Word","Excel","Chrome"]};let results=[],allCorrect=!0;document.querySelectorAll(".drop-zone").forEach(zone=>{let zoneId=zone.id,itemsInZone;Array.from(zone.children).map(item=>item.innerText).forEach(item=>{let isCorrect=correctAnswers[zoneId].includes(item);results.push({item:item,category:zoneId,correct:isCorrect});let draggedItem=document.querySelector(`.drag-item[data-id="${item}"]`);draggedItem?draggedItem.classList.add(isCorrect?"correct":"incorrect"):console.error(`No se encontró el elemento: ${item}`),isCorrect||(allCorrect=!1)})}),document.getElementById("feedback").innerText=allCorrect?"¡Todo correcto!":"Algunas respuestas son incorrectas.",enviarA_xAPI(results)}function enviarA_xAPI(results){const statement={actor:{mbox:"mailto:usuario@example.com",name:"Usuario",objectType:"Agent"},verb:{id:"http://adlnet.gov/expapi/verbs/completed",display:{"en-US":"completed"}},object:{id:"http://example.com/drag-and-drop",definition:{name:{"en-US":"Drag and Drop Exercise"},description:{"en-US":"User categorized items as Software or Hardware."}}},result:{success:results.every(r=>r.correct),response:JSON.stringify(results)}};ADL.XAPIWrapper.sendStatement(statement)}document.addEventListener("DOMContentLoaded",()=>{const items=document.querySelectorAll(".drag-item"),dropZones=document.querySelectorAll(".drop-zone");console.log("Items:",items),console.log("Drop Zones:",dropZones),items.forEach(item=>{item.addEventListener("dragstart",event=>{event.dataTransfer.setData("text",event.target.dataset.type),event.dataTransfer.setData("id",event.target.innerText)})}),dropZones.forEach(zone=>{zone.addEventListener("dragover",event=>{event.preventDefault()}),zone.addEventListener("drop",event=>{event.preventDefault();const type=event.dataTransfer.getData("text"),id=event.dataTransfer.getData("id"),draggedItem=Array.from(document.querySelectorAll(`.drag-item[data-type="${type}"]`)).find(item=>item.innerText.trim()===id.trim());draggedItem?event.target.appendChild(draggedItem):console.error("Elemento no encontrado:",type,id)})})});